import './App.css';
import React, { useEffect, useState } from 'react'
import Header from './components/Header'
import Main from './components/Main'
import LinkForm from './components/LinkForm'
import DisplayLinks from './components/DisplayLinks'
import axios from 'axios'

const  App = () => {

  const [longLink, setLongLink] = useState('')
  const [shortLink, setShortLink] = useState('')
  const [displayedLinks, setDisplayedLinks] = useState([])

  const API_URL = `https://api.shrtco.de/v2/shorten?url=`

  const changeHandler = (e) => {
    setLongLink(e.target.value)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    if(longLink === ''){
      alert('input must be a valid URL')
    } else {
      axios.post(`${API_URL}${longLink}`)
        .then(res => {
          console.log(res)
          // const data = res.data.result
          // setShortLink(data.short_link)
          // setLongLink('')
        })
    }
  }

  //push the shortened link to array whenever shortLink is updated
  useEffect(() => {
    if(shortLink !== ''){
      setDisplayedLinks( d => [...d, shortLink])
    }
  }, [shortLink])

  return (
    <div className="App">
      <Header />
      <Main />
      <LinkForm onSubmit={onSubmit} changeHandler={changeHandler} value={longLink}/>
      <DisplayLinks links={displayedLinks}/>
    </div>
  );
}

export default App;
