import './App.css';
import React, { useEffect, useState } from 'react'
import Header from './components/Header'
import LinkInput from './components/LinkInput'
import DisplayLinks from './components/DisplayLinks'
import axios from 'axios'

const  App = () => {

  const [longLink, setLongLink] = useState('')
  const [shortLink, setShortLink] = useState('')
  const [displayedLinks, setDisplayedLinks] = useState([])

  const API_URL = `https://api.shrtco.de/v2/shorten?url=`

  const changeHandler = (e) => {
    setLongLink(e.target.value)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    if(longLink === ''){
      alert('input must be a valid URL')
    } else {
      axios.get(`${API_URL}${longLink}`)
        .then(res => {
          const data = res.data.result
          setShortLink(data.short_link)
        })
    }
    setLongLink('')
  }

  //push the shortened link to array whenever shortLink is updated
  useEffect(() => {
    if(shortLink !== ''){
      setDisplayedLinks( d => [...d, shortLink])
    }
  }, [shortLink])



  return (
    <div className="App">

      <Header />

      <div className="main-content">

        <div className="call-to-action">
          <h1>More than just shorter links</h1>
          <p>
            Build your brand's recognition and get 
            detailed insights on how links are performing.
          </p>
          <button className="button">Get Started</button>
        </div>

      </div>

      <LinkInput changeHandler={changeHandler} onSubmit={onSubmit} value={longLink}/>

      <DisplayLinks />

      <div className="features">
        <h2>Advanced Statistics</h2>
        <p>Track how your links are performing across the web 
          with our advanced statistics dashboard.
        </p>
      </div>

    </div>
  );
}

export default App;
