import React from 'react'

const LinkCard = ({link}) => {
    //console.log(link)

    const copyUrl = (link) => {
        const url = link
        const button = document.querySelector(".copy-button")
        document.execCommand(url)
        // button.style.backgroundColor = "black"
        // button.innerTextNode("Coped!")
    }

    return(
        <div className="link-card">
            <p>{link.long}</p>
            <div className="copy-link">
                <p>{link.short}</p>
                <button className="button copy-button" onClick={() => copyUrl(link.short)} >Copy!</button>
            </div>
        </div>
    )
}

export default LinkCard
