import './App.css';
import React, { useEffect, useState } from 'react'
import Header from './components/Header'
import LinkForm from './components/LinkForm'
import Hero from './components/Hero'
import Info from './components/Info'
import Footer from './components/Footer'
import axios from 'axios'

const  App = () => {

  const [longLink, setLongLink] = useState('')
  const [shortLink, setShortLink] = useState('')
  const [displayedLinks, setDisplayedLinks] = useState([])

  const API_URL = `https://api.shrtco.de/v2/shorten?url=`

  const changeHandler = (e) => {
    setLongLink(e.target.value)
  }

  const onSubmit = (e) => {
    e.preventDefault()

    if(longLink === ''){
      alert('input must be a valid URL')
    } else {
      axios.get(`${API_URL}${longLink}`)
        .then(res => {
          const data = res.data.result
          setShortLink(data.short_link)
          setLongLink('')
        })
    }
  }

  //push the shortened link to array whenever shortLink is updated
  useEffect(() => {
    const obj = {
      long: longLink,
      short: shortLink
    }

    if(shortLink !== ''){
      setDisplayedLinks( d => [...d, obj])
    }
  }, [shortLink, longLink])

  return (
    <div className="App">
      <Header />
      <Hero/>
      <LinkForm onSubmit={onSubmit} changeHandler={changeHandler} value={longLink} links={displayedLinks}/>
      <Info />
      <Footer />
    </div>
  );
}

export default App;
