import React from 'react';
import DisplayLinks from './DisplayLinks'

const LinkForm = ({ onSubmit, changeHandler, value, links }) => {
    return(
        <div className="link-input">
            <form onSubmit={onSubmit}>
                <input placeholder="Shorten a link here..." onChange={changeHandler} value={value}></input>
                <button className="submit-button" type="submit">Shorten It!</button>
            </form>
            <DisplayLinks links={links} />
        </div>
    )
}

export default LinkForm
