import React, {useEffect, useState} from 'react';
import axios from 'axios'
import DisplayLinks from './DisplayLinks'
import Loader from 'react-loader-spinner'

const LinkForm = () => {
    const [url, setUrl] = useState('')
    const [shortUrl, setShortUrl] = useState('')
    const [displayedLinks, setDisplayedLinks] = useState([])
    const [isLoading, setIsLoading] = useState(false)
    const API_URL = `https://api.shrtco.de/v2/shorten?url=`

    const changeHandler = (e) => {
    setUrl(e.target.value)
    }

    const onSubmit = (e) => {
    e.preventDefault()

    if(url === ''){
        alert('input must be a valid URL')
    } else {
        setIsLoading(true)
        axios.get(`${API_URL}${url}`)
        .then(res => {
            const data = res.data.result
            setIsLoading(false)
            setShortUrl(data.short_link)
            setUrl('')
        })
    }
    }

    //push the shortened link to array whenever shortLink is updated
    useEffect(() => {
    const obj = {
        long: url,
        short: shortUrl
    }

    if(shortUrl !== ''){
        setDisplayedLinks( d => [...d, obj])
    }
    }, [shortUrl, url])

    return(
        <div className="link-input">
            <form onSubmit={onSubmit}>
                <input placeholder="Shorten a link here..." onChange={changeHandler} value={url}></input>
                <button className="submit-button" type="submit">Shorten It!</button>
            </form>
            { isLoading ? <Loader type="Oval" color="#00BFFF" height={30} width={30} className="loader" /> : null}
            <DisplayLinks links={displayedLinks} />
        </div>
    )
}

export default LinkForm
